import React, { Component } from 'react';
import auth0Client from '../Auth';
import Task from './Task';
import axios from 'axios';
import { withRouter } from 'react-router-dom';

class TaskList extends Component {
    constructor(props) {
        super(props);

        this.addTask = this.addTask.bind(this);

        this.state = {
            taskList: [],
        }
    }

    async componentDidMount() {
        if (!auth0Client.isAuthenticated()) {
            this.props.history.push('/');
            return;
        }

        const questions = (await axios.get('http://localhost:8081/app')).data;
        console.log(questions);
        console.log(questions.todoTasks);

        this.setState({
            taskList: questions.todoTasks,
        })
    }

    async refreshTasks() {
        const questions = (await axios.get(`http://localhost:8081/app`)).data;
        this.setState({
            taskList: questions.todoTasks,
        });
    }

    async addTask(e) {

        e.preventDefault();
        console.log(auth0Client.getProfile());
        if (this._inputElement !== "") {
            var newTask = {
                userId: auth0Client.getProfile().sub,
                content: this._inputElement.value,
            }
            console.log(newTask);

            await axios.post('http://localhost:8081/app/newTask', newTask,
            {
                headers: {'Authorization': `Bearer ${auth0Client.getIdToken()}`}
            });
            this.refreshTasks();

            // this.setState((prevState) => {
            //     return {
            //         taskList: prevState.taskList.concat(newTask),
            //     };
            // })
        }

        console.log(this.state.taskList);
    }

    render() {
        console.log(this.state.taskList);
        return (
            <div className="container">
                <div className="TaskList">
                    <div>
                        <form onSubmit={this.addTask}>
                            <input placeholder="Add new Task" ref={(a) => this._inputElement = a}
                                type="text"></input>
                            <button type="submit">Add</button>
                        </form>
                    </div>
                    {
                        this.state.taskList.map((task) => <Task key={task.date} date={task.date} dueDate={task.dueDate} content={task.content} />)
                    }
                </div>
            </div>
        )
    }
}

export default withRouter(TaskList);
