import React, {Component} from 'react';
import {withRouter} from 'react-router-dom';
import auth0Client from '../Auth';
import axios from 'axios';

class NewQuestion extends Component {
    constructor(props) {
        super(props);

        this.state = {
            disabled: false,
            title: '',
            description: '',
        };
    }

    updateDescription(value) {
        this.setState({
            description: value,
        });
    }

    updateTitle(value) {
        this.setState({
            description: value,
        });
    }

    async submit() {
        this.setState({
            disabled: true,
        });

        await axios.post('http://localhost:8081', {
            title: this.state.title,
            description: this.state.description,
        }, {
            headers: {'Authorization': `Bearer ${auth0Client.getIdToken()}`}
        });

        this.props.history.push('/');
    }

    render() {
        return (
            <div className="container">
                <div className="row">
                    <div className="col-12">
                        <div className="card border-primary">
                            <div className="card-header">New Question</div>
                            <div className="card-body text-left">
                                <div className="form-group">
                                    <label htmlFor="exampleInputEmail1">Title:</label>
                                    <input
                                       disabled={this.state.disabled}
                                       type="text"
                                       onBlur={(e) => {this.updateTitle(e.target.value)}}
                                       className="form-control"
                                       placerholder="Give your question a title"
                                    />
                                </div>
                                <div className="form-group">
                                    <label htmlFor="exampleInputEmail1">Description:</label>
                                    <input
                                       disabled={this.state.disabled}
                                       type="text"
                                       onBlur={(e) => {this.updateDescription(e.target.value)}}
                                       className="form-control"
                                       placeholder="Give more context to your question"
                                    />
                                </div>
                                <button
                                    disabled={this.state.disabled}
                                    className="btn btn-primary"
                                    onClick={() => {this.submit()}}>
                                    Submit
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default withRouter(NewQuestion);
