import React, { Component } from 'react';
import DateUI from '../components/dateUI/dateUI';
import './Task.scss';
import IconButton from '@material-ui/core/Button';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';

class Task extends Component {
    constructor(props) {
        super(props);

        this.deleteSelf = this.deleteSelf.bind(this);
        var date = this.props.dueDate && new Date(this.props.dueDate);

        this.state = {
            date: this.props.date,
            // dueDate = this.props.dueDate,
            content: this.props.content,
            _id: this.props.id,
            month: date && (date.getMonth() + 1),
            day: date && date.getDate(),
            year: date && date.getFullYear(),
            date: date,
            // dueDate: this.props.dueDate && Date(this.props.dueDate),
        }
    }

    deleteSelf() {
        this.props.remove(this.state._id);
    }

    render() {
        return (
            <li>
                <div className="Task clickable" onClick={() => this.deleteSelf()} key={this.state.date}>
                    <div className="taskContent">{this.state.content}</div>
                    <div className="right-aligned row">

                    {this.state.date &&
                        <DateUI
                            month={this.state.month}
                            day={this.state.day}
                            year={this.state.year}
                        />
                    }
                    {/* <Button>{MoreVertIcon}</Button> */}
                    <IconButton aria-label="delete">
                        <MoreHorizIcon />
                    </IconButton>
                    </div>
                </div>
            </li>
        )
    }
}

export default Task;
