import React, { Component } from 'react';
import DateUI from '../components/dateUI/dateUI';
import './Task.scss';
import IconButton from '@material-ui/core/Button';
import MoreHorizIcon from '@material-ui/icons/MoreHoriz';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

function TaskMenu() {
  const [anchorEl, setAnchorEl] = React.useState(null);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <div>
      {/* <Button aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
        Open Menu
      </Button> */}
        <IconButton aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
            <MoreHorizIcon />
        </IconButton>
    <Menu
        id="simple-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
        >
        <MenuItem onClick={() => this.props.remove()}>Delete Task</MenuItem>
      </Menu>
    </div>
  );
}

class Task extends Component {
    constructor(props) {
        super(props);

        this.deleteSelf = this.deleteSelf.bind(this);
        this.completeSelf = this.completeSelf.bind(this);
        var date = this.props.dueDate && new Date(this.props.dueDate);

        this.state = {
            date: this.props.date,
            // dueDate = this.props.dueDate,
            content: this.props.content,
            _id: this.props.id,
            month: date && (date.getMonth() + 1),
            day: date && date.getDate(),
            year: date && date.getFullYear(),
            date: date,
            // dueDate: this.props.dueDate && Date(this.props.dueDate),
        }
    }

    completeSelf() {
        this.props.complete(this.state._id);
    }

    deleteSelf() {
        this.props.remove(this.state._id);
    }

    render() {
        return (
            <li>
                <div onClick={() => this.completeSelf()} key={this.state.date} className="Task clickable" >
                    <div  className="taskContent">{this.state.content}</div>
                    <div className="right-aligned row">

                    {this.state.date &&
                        <DateUI
                            month={this.state.month}
                            day={this.state.day}
                            year={this.state.year}
                        />
                    }
                    {/* <Button>{MoreVertIcon}</Button> */}
                    <TaskMenu remove={this.deleteSelf} />
                    </div>
                </div>
            </li>
        )
    }
}

export default Task;
