import React, { Component } from 'react';
import auth0Client from '../Auth';
import axios from 'axios';
import { withRouter } from 'react-router-dom';
import moment from 'moment';

class Week extends Component {
    constructor(props) {
        super(props);

        this.filterTasks = this.filterTasks.bind(this);

        this.state = {
            sunday: [],
            monday: [],
            tuesday: [],
            wednesday: [],
            thursday: [],
            friday: [],
            saturday: [],
        }
    }

    async componentDidMount() {
        if (!auth0Client.isAuthenticated) {
            this.props.history.push('/');
            return;
        }

        const tasks = await axios.post(`http://localhost:8081/app`, {
            userId: auth0Client.getProfile().sub,
        }, {
            headers: {'Authorization': `Bearer ${auth0Client.getIdToken()}`}
        });

        var taskList = tasks.data.todoTasks;
        this.filterTasks(taskList);
    }

    filterTasks(taskList) {

        var sunday = [];
        var monday = [];
        var tuesday = [];
        var wednesday = [];
        var thursday = [];
        var wednesday = [];
        var thursday = [];
        var friday = [];
        var saturday = [];

        for (var i = 0; i < taskList; i++) {
            var curr = taskList[i];
            if (curr.dueDate !== null) {
                var now = moment();
                var input = moment(curr.dueDate);
                var isThisWeek = (now.isoWeek() == input.isoWeek());

                if (isThisWeek) {
                    switch(input.day()) {
                        case 0:
                            sunday.push(curr);
                            break;
                        case 1:
                            monday.push(curr);
                            break;
                        case 2:
                            tuesday.push(curr);
                            break;
                        case 3:
                            wednesday.push(curr);
                            break;
                        case 4:
                            thursday.push(curr);
                            break;
                        case 5:
                            friday.push(curr);
                            break;
                        case 6:
                            saturday.push(curr);
                            break;
                    }
                }

                this.setState({
                    sunday: sunday,
                    monday: monday,
                    tuesday: tuesday,
                    wednesday: wednesday,
                    thursday: thursday,
                    friday: friday,
                    saturday: saturday,
                })

                console.log(this.state);
            }
        }
    }

}

export default Week;
