import React, { Component } from 'react';
import auth0Client from '../Auth';
import Task from './Task';
import axios from 'axios';
import { withRouter } from 'react-router-dom';
import FlipMove from "react-flip-move"
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import './TaskList.scss';
import Button from '@material-ui/core/Button';

class TaskList extends Component {
    constructor(props) {
        super(props);

        this.addTask = this.addTask.bind(this);
        this.deleteTask = this.deleteTask.bind(this);
        this.completeTask = this.completeTask.bind(this);
        this.updateDate = this.updateDate.bind(this);

        this.state = {
            taskList: [],
            uncompletedTasks: [],
            completedTasks: [],
            selectedDate: null,
        }
    }

    updateDate(date) {
        this.setState({
            selectedDate: date,
        })
    }

    async componentDidMount() {
        if (!auth0Client.isAuthenticated()) {
            this.props.history.push('/');
            return;
        }

        // const tasks = (await axios.get(`http://localhost:8081/app/${auth0Client.getProfile().sub}`)).data;
        const tasks = await axios.post(`http://localhost:8081/app/`, {
            userId: auth0Client.getProfile().sub,
        }, {
            headers: {'Authorization': `Bearer ${auth0Client.getIdToken()}`}
        });
        // tasks = tasks.data;
        // console.log(tasks.data);
        // console.log(questions);
        // console.log(questions.todoTasks);
        var completedTasks = [];
        var uncompletedTasks = [];
        for (var i = 0; i < tasks.data.todoTasks.length; i++) {
            if (tasks.data.todoTasks[i].completed) {
                completedTasks.push(tasks.data.todoTasks[i]);
            } else {
                uncompletedTasks.push(tasks.data.todoTasks[i]);
            }
        }

        this.setState({
            taskList: tasks.data.todoTasks,
            uncompletedTasks: uncompletedTasks,
            completedTasks: completedTasks,
        })
    }

    async refreshTasks() {
        const tasks = await axios.post(`http://localhost:8081/app/`, {
            userId: auth0Client.getProfile().sub,
        }, {
            headers: {'Authorization': `Bearer ${auth0Client.getIdToken()}`}
        });

        var completedTasks = [];
        var uncompletedTasks = [];
        for (var i = 0; i < tasks.data.todoTasks.length; i++) {
            if (tasks.data.todoTasks[i].completed) {
                completedTasks.push(tasks.data.todoTasks[i]);
            } else {
                uncompletedTasks.push(tasks.data.todoTasks[i]);
            }
        }

        this.setState({
            taskList: tasks.data.todoTasks,
            uncompletedTasks: uncompletedTasks,
            completedTasks: completedTasks,
        })
    }

    async deleteTask(key) {
        const attempt = await axios.post('http://localhost:8081/app/deleteTask', {
            key: key,
        }, {
            headers: {'Authorization': `Bearer ${auth0Client.getIdToken()}`}
        });
        this.refreshTasks();
    }

    async completeTask(key) {
        const attempt = await axios.post('http://localhost:8081/app/completeTask', {
            key: key,
        }, {
            headers: {'Authorization': `Bearer ${auth0Client.getIdToken()}`}
        });
        this.refreshTasks();
    }

    async addTask(e) {

        e.preventDefault();
        // console.log(auth0Client.getProfile());
        if (this._inputElement !== "") {
            var newTask = {
                userId: auth0Client.getProfile().sub,
                content: this._inputElement.value,
                dueDate: this.state.selectedDate,
            }
            // console.log(newTask);

            await axios.post('http://localhost:8081/app/newTask', newTask,
            {
                headers: {'Authorization': `Bearer ${auth0Client.getIdToken()}`}
            });

            this.refreshTasks();

            this._inputElement.value = "";

            // this.setState((prevState) => {
            //     return {
            //         taskList: prevState.taskList.concat(newTask),
            //     };
            // })
        }
    }

    render() {
        // console.log(this.state.taskList);
        return (
            <div className="container">
                <div className="TaskList">
                    <ul>
                        <FlipMove duration={250} easing="ease">
                        {
                            this.state.uncompletedTasks.map(
                                (task) => <Task 
                                            key={task.date} 
                                            date={task.date} 
                                            remove={this.completeTask} 
                                            id={task._id} 
                                            dueDate={task.dueDate} 
                                            content={task.content} />
                                            )
                        }
                        </FlipMove>
                    </ul>
                    <div>
                        <form onSubmit={this.addTask} id="addTaskForm" className="form-group">
                            <input className="" placeholder="Add new Task" ref={(a) => this._inputElement = a}
                                type="text"></input>
                            <DatePicker placeholderText="Due Date" selected={this.state.selectedDate} onChange={(date) => {this.updateDate(date)}} />
                            <Button variant="contained" onClick={() => {
                                // document.getElementById('addTaskForm').submit()
                                this.addTask
                            }}>Add</Button>
                        </form>
                    </div>
                    <div>Completed</div>
                    <ul>
                        <FlipMove duration={250} easing="ease">
                        {
                            this.state.completedTasks.map(
                                (task) => <Task 
                                            key={task.date} 
                                            date={task.date} 
                                            remove={this.completeTask} 
                                            id={task._id} 
                                            dueDate={task.dueDate} 
                                            content={task.content} />
                                            )
                        }
                        </FlipMove>

                    </ul>
                </div>
            </div>
        )
    }
}

export default withRouter(TaskList);
