import React, { Component } from 'react';
import auth0Client from '../Auth';
import Task from './Task';

class TaskList extends Component {
    constructor(props) {
        super(props);

        this.addTask = this.addTask.bind(this);

        this.state = {
            taskList: [],
        }
    }

    async addTask(e) {

        if (this._inputElement !== "") {
            var newTask = {
                userId: auth0Client.getProfile().user_id,
                content: this._inputElement.value,
            }

            await axios.post('http://localhost:8081/app/newTask', newTask,
            {
                headers: {'Authorization': `Bearer ${auth0Client.getIdToken()}`}
            });

            this.setState((prevState) => {
                return {
                    taskList: prevState.taskList.concat(newTask),
                };
            })
        }

        console.log(this.state.taskList);
        e.preventDefault();
    }

    render() {
        return (
            <div class="container">
                <div class="TaskList">
                    <div>
                        <form onSubmit={this.addTask}>
                            <input placeholder="Add new Task" ref={(a) => this._inputElement = a}
                                type="text"></input>
                            <button type="submit">Add</button>
                        </form>
                    </div>
                    {
                        this.state.taskList.map((task) => <Task date={task.date} dueDate={task.dueDate} content={task.content} />)
                    }
                </div>
            </div>
        )
    }
}
