import React, { Component } from 'react';
import auth0Client from '../Auth';
import axios from 'axios';
import { withRouter } from 'react-router-dom';
import moment from 'moment';
import Task from '../Tasks/Task';

class Week extends Component {
    constructor(props) {
        super(props);

        this.filterTasks = this.filterTasks.bind(this);

        this.state = {
            sunday: [],
            monday: [],
            tuesday: [],
            wednesday: [],
            thursday: [],
            friday: [],
            saturday: [],
        }
    }

    async componentDidMount() {
        if (!auth0Client.isAuthenticated()) {
            this.props.history.push('/');
            return;
        }

        const tasks = await axios.post(`http://localhost:8081/app`, {
            userId: auth0Client.getProfile().sub,
        }, {
            headers: {'Authorization': `Bearer ${auth0Client.getIdToken()}`}
        });

        var taskList = tasks.data.todoTasks;
        this.filterTasks(taskList);
    }

    filterTasks(taskList) {

        var sunday = [];
        var monday = [];
        var tuesday = [];
        var wednesday = [];
        var thursday = [];
        var wednesday = [];
        var thursday = [];
        var friday = [];
        var saturday = [];
        console.log(taskList);

        for (var i = 0; i < taskList.length; i++) {
            var curr = taskList[i];
            if (curr.dueDate !== null) {
                var now = moment();
                var input = moment(curr.dueDate);
                var isThisWeek = (now.isoWeek() == input.isoWeek());

                if (isThisWeek) {
                    switch(input.day()) {
                        case 0:
                            sunday.push(curr);
                            break;
                        case 1:
                            monday.push(curr);
                            break;
                        case 2:
                            tuesday.push(curr);
                            break;
                        case 3:
                            wednesday.push(curr);
                            break;
                        case 4:
                            thursday.push(curr);
                            break;
                        case 5:
                            friday.push(curr);
                            break;
                        case 6:
                            saturday.push(curr);
                            break;
                    }
                }

                this.setState({
                    sunday: sunday,
                    monday: monday,
                    tuesday: tuesday,
                    wednesday: wednesday,
                    thursday: thursday,
                    friday: friday,
                    saturday: saturday,
                })

            }
        }
    }

    taskFunction = (task) => <Task
                                key={task.date} 
                                date={task.date} 
                                edit={this.editTask}
                                complete={this.completeTask} 
                                remove={this.deleteTask} 
                                id={task._id} 
                                task={task} />

    render () {
        var week = [this.state.saturday,
                    this.state.monday,
                    this.state.tuesday,
                    this.state.wednesday,
                    this.state.thursday,
                    this.state.friday,
                    this.state.sunday]
        console.log(week);
        var mappedWeek = [];
        for (var i = 0; i < week.length; i++) {
            mappedWeek.push(week[i].map(
                    (task) => <Task 
                                key={task.date} 
                                date={task.date} 
                                edit={this.editTask}
                                complete={this.completeTask} 
                                remove={this.deleteTask} 
                                id={task._id} 
                                task={task} />

            ))
        }
        console.log(mappedWeek);
        return (
            <div className="">
                { this.state.saturday.map(this.taskFunction) }
                { this.state.monday.map(this.taskFunction) }
                { this.state.tuesday.map(this.taskFunction) }
                { this.state.wednesday.map(this.taskFunction) }
                { this.state.thursday.map(this.taskFunction) }
                { this.state.friday.map(this.taskFunction) }
                { this.state.sunday.map(this.taskFunction) }
                {mappedWeek[0]}
                {/* {mappedWeek[1]} */}
                {mappedWeek[2]}
                {mappedWeek[3]}
            </div>
        )
    }

}

export default Week;
