import React, { Component } from 'react';
import auth0Client from '../Auth';
import axios from 'axios';
import { withRouter } from 'react-router-dom';
import moment from 'moment';
import Task from '../Tasks/Task';
import './week.scss';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { CardHeader } from '@material-ui/core';

class Day extends Component {
    constructor(props) {
        super(props);

        this.state = {
            tasks: this.props.tasks,
            day: this.props.day,
        }
    }

    renderTask = (task) => <Task
                                key={task.date} 
                                edit={this.editTask}
                                complete={this.completeTask} 
                                remove={this.deleteTask} 
                                task={task} />


    render() {
        return (
            <Card variant="outlined">
                <CardHeader title={this.state.day}>{this.state.day}</CardHeader>
                <CardContent>
                    {this.state.tasks.map(this.renderTask)}
                </CardContent>
            </Card>
        )
    }
}

class Week extends Component {
    constructor(props) {
        super(props);

        this.filterTasks = this.filterTasks.bind(this);

        this.state = {
            sunday: [],
            monday: [],
            tuesday: [],
            wednesday: [],
            thursday: [],
            friday: [],
            saturday: [],
        }
    }

    async componentDidMount() {
        if (!auth0Client.isAuthenticated()) {
            this.props.history.push('/');
            return;
        }

        const tasks = await axios.post(`http://localhost:8081/app`, {
            userId: auth0Client.getProfile().sub,
        }, {
            headers: {'Authorization': `Bearer ${auth0Client.getIdToken()}`}
        });

        var taskList = tasks.data.todoTasks;
        this.filterTasks(taskList);
    }

    filterTasks(taskList) {

        var sunday = [];
        var monday = [];
        var tuesday = [];
        var wednesday = [];
        var thursday = [];
        var wednesday = [];
        var thursday = [];
        var friday = [];
        var saturday = [];
        console.log(taskList);

        for (var i = 0; i < taskList.length; i++) {
            var curr = taskList[i];
            if (curr.dueDate !== null) {
                var now = moment();
                var input = moment(curr.dueDate);
                var isThisWeek = (now.isoWeek() == input.isoWeek());

                if (isThisWeek) {
                    switch(input.day()) {
                        case 0:
                            sunday.push(curr);
                            break;
                        case 1:
                            monday.push(curr);
                            break;
                        case 2:
                            tuesday.push(curr);
                            break;
                        case 3:
                            wednesday.push(curr);
                            break;
                        case 4:
                            thursday.push(curr);
                            break;
                        case 5:
                            friday.push(curr);
                            break;
                        case 6:
                            saturday.push(curr);
                            break;
                    }
                }

                this.setState({
                    sunday: sunday,
                    monday: monday,
                    tuesday: tuesday,
                    wednesday: wednesday,
                    thursday: thursday,
                    friday: friday,
                    saturday: saturday,
                })

            }
        }
    }

    taskFunction = (task) => <Task
                                key={task.date} 
                                edit={this.editTask}
                                complete={this.completeTask} 
                                remove={this.deleteTask} 
                                task={task} />

    render () {

        return (
            <div className="container">

            <div className="weekly">
                    {/* <div className="day">Saturday
                        <ul>
                            { this.state.saturday.map(this.taskFunction) }
                        </ul>
                    </div> */}
                    <Day day="Saturday" tasks={this.state.saturday} />
                    <Day day="Monday" tasks={this.state.monday} />
                    <Day day="Tuesday" tasks={this.state.tuesday} />
                    <Day day="Saturday" tasks={this.state.saturday} />
                    <Day day="Saturday" tasks={this.state.saturday} />
            </div>
            </div>
        )
    }

}

export default Week;
