import React, { Component } from 'react';
import Task from '../Tasks/Task';
import { makeStyles } from '@material-ui/core/styles';

import './week.scss';

import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { CardHeader } from '@material-ui/core';

const useStyles = makeStyles({
    root: {
      minWidth: 275,
    },
    bullet: {
      display: 'inline-block',
      margin: '0 2px',
      transform: 'scale(0.8)',
    },
    title: {
      fontSize: 14,
    },
    pos: {
      marginBottom: 12,
    },
  });

class Day extends Component {

    constructor(props) {
        super(props);
        console.log("but ", props);

        const classes = useStyles();

        this.state = {
            tasks: this.props.tasks,
            day: this.props.day,
            classes: classes,
        }
    }


    renderTask = (task) => <Task
                                key={task.date} 
                                edit={this.editTask}
                                complete={this.completeTask} 
                                remove={this.deleteTask} 
                                task={task} />


    render() {
        return (
            <Card>
                <CardHeader title={this.state.day}>{this.state.day}
                <Typography className={this.state.classes.title} color="textSecondary" gutterBottom>
                    Word of the Day
                </Typography>
                    </CardHeader>
                <CardContent>
                    <ul>
                    {this.props.tasks.map((task) => this.renderTask(task))}
                    </ul>
                </CardContent>
            </Card>
        )
    }
}

export default Day;
