import React, { Component } from 'react';

class Task extends Component {
    constructor(props) {
        super(props);

        this.deleteSelf = this.deleteSelf.bind(this);
        var date = this.props.dueDate && new Date(this.props.dueDate);

        this.state = {
            date: this.props.date,
            // dueDate = this.props.dueDate,
            content: this.props.content,
            _id: this.props.id,
            month: date && date.getMonth(),
            day: date && date.getDay(),
            // dueDate: this.props.dueDate && Date(this.props.dueDate),
        }
    }

    deleteSelf() {
        this.props.remove(this.state._id);
    }

    render() {
        return (
            <li>
                <div className="Task clickable" onClick={() => this.deleteSelf()} key={this.state.date}>
                    {this.state.content}
                    {this.state.dueDate &&
                        <div className="Date">
                            {this.state.month}/{this.state.day}
                        </div>
                    }
                </div>
            </li>
        )
    }
}

export default Task;
